function deltas = deltas(frames)  deltas = zeros(size(frames));    cantFilters = size(frames)(2);    for i=1:size(frames)(1)    deltas(i,1) = (2*(frames(1,3) - frames(1,1)) + (frames(1,2) - frames(1,1))) /10;    deltas(i,2) = (2*(frames(1,4) - frames(1,1)) + (frames(1,3) - frames(1,1))) /10;    for j=3:cantFilters-2      deltas(i,j) = (2*(frames(1,j+2) - frames(1,j-2)) + (frames(1,j+1) - frames(1,j-1))) /10;    end    deltas(i,cantFilters-1) = (2*(frames(1,cantFilters) - frames(1,cantFilters-3)) + (frames(1,cantFilters) - frames(1,cantFilters-2))) /10;    deltas(i,cantFilters) = (2*(frames(1,cantFilters) - frames(1,cantFilters-2)) + (frames(1,cantFilters) - frames(1,cantFilters-1))) /10;      endend