function melCeptrums = melCeptrums(frames)    filters = size(frames)(2);  cantFrames = size(frames)(1);  melCeptrums = zeros(size(frames)(1), ceil(filters/2));      for i=1:cantFrames    for j=1:ceil(filters/2)      aux = 0;      aux2 = frames(i,:);      for k=1:filters        aux += log(aux2(1,k))*cos(j*(k-0.5)*(pi/filters));      end      melCeptrums(i,j) = aux;    end  endend